<?php\n\nfunction processRSSFeed($raw_xml) {\n    $xml = simplexml_load_string($raw_xml);\n    foreach ($xml->channel->item as $item) {\n        // Process each item in the feed\n        echo "Title: " . $item->title . "<br>";\n        echo "Link: " . $item->link . "<br><br>";\n    }\n}\n\n// First RSS Feed\n$raw_xml1 = file_get_contents('path/to/first/rss_feed.xml');\nprocessRSSFeed($raw_xml1);\n\n// Second RSS Feed\n$raw_xml2 = file_get_contents('path/to/second/rss_feed.xml');\nprocessRSSFeed($raw_xml2);\n\n?>