function preprocess_and_sanitize($key, $default, $filter = FILTER_SANITIZE_STRING) {\n    if (empty($_GET[$key])) {\n        $_GET[$key] = $default;\n    }\n    // Additional preprocessing can be done here\n    $_GET[$key] = str_replace(' ', '', $_GET[$key]);\n    \n    // Sanitize the final value\n    return filter_var($_GET[$key], $filter);\n}\n\n// Usage\n$name = preprocess_and_sanitize('name', '(default name)');\necho $name;