<?php\n// Database connection\n$pdo = new PDO('mysql:host=localhost;dbname=your_database', 'username', 'password');\n\n// Define the number of items per page\n$itemsPerPage = 20;\n\n// Get the current page number from the query string (default is 1)\n$page = isset($_GET['page']) ? (int)$_GET['page'] : 1;\n\n// Calculate the offset for the SQL query\n$offset = ($page - 1) * $itemsPerPage;\n\n// Query to count the total number of items\n$countQuery = $pdo->query("SELECT COUNT(*) FROM answers WHERE question_id = :question_id");\n$countQuery->execute(['question_id' => $questionId]);\n$totalItems = $countQuery->fetchColumn();\n\n// Calculate the total number of pages\n$totalPages = ceil($totalItems / $itemsPerPage);\n\n// Query to fetch the items for the current page\n$itemsQuery = $pdo->prepare("SELECT * FROM answers WHERE question_id = :question_id LIMIT :limit OFFSET :offset");\n$itemsQuery->bindValue(':question_id', $questionId, PDO::PARAM_INT);\n$itemsQuery->bindValue(':limit', $itemsPerPage, PDO::PARAM_INT);\n$itemsQuery->bindValue(':offset', $offset, PDO::PARAM_INT);\n$itemsQuery->execute();\n\n// Fetch the results\n$items = $itemsQuery->fetchAll(PDO::FETCH_ASSOC);\n\n// Display the items and pagination links\nforeach ($items as $item) {\n    echo "<p>{$item['answer']}</p>";\n}\n\necho '<div class="pagination">';\nfor ($i = 1; $i <= $totalPages; $i++) {\n    echo '<a href="?page=' . $i . '">' . $i . '</a> ';\n}\necho '</div>';\n?>