class nicnames_config {\n    public static $resourcetypes = [\n        'name_type' => [\n            'type1' => 'Type 1',\n            'type2' => 'Type 2'\n        ]\n    ];\n}\n\nfunction getstrings($type, $givennames, $surname, $surnamefirst, $title) {\n    $types = nicnames_config::$resourcetypes;\n\n    $nameType = isset($types['name_type'][$type]) ? $types['name_type'][$type] : $type;\n    $givens = $givennames == '' ? null : $givennames;\n    \n    return array(\n        'title' => $surnamefirst ? ($surname . ',') : $givens,\n        'subtitle' => $surnamefirst ? $givens : $surname,\n        'pre-qualifier' => $nameType,\n        'post-qualifier' => $title == '' ? null : ('(' . $title . ')'),\n    ) + getcommonstrings();\n}\n\nfunction getcommonstrings() {\n    // Dummy function for additional common strings\n    return [];\n}