$items = array(/* 85 items */);\n\nforeach ($items as $item1) {\n    generate(array($item1));\n    foreach ($items as $item2) {\n        if ($item2['id'] > $item1['id']) {\n            generate(array($item1, $item2));\n            foreach ($items as $item3) {\n                if ($item3['id'] > $item2['id']) {\n                    generate(array($item1, $item2, $item3));\n                }\n            }\n        }\n    }\n}\n\nfunction generate($items_array) {\n    $temp_array = array();\n    foreach ($items_array as $item) {\n        $temp_array[] = $item['id'];\n    }\n\n    // No need to sort as we ensure the order in the loop\n    $index = implode("_", $temp_array);\n\n    // Insert directly to the database\n    $db = new PDO('mysql:host=localhost;dbname=test', 'username', 'password');\n    $stmt = $db->prepare("INSERT INTO `combos` (combo_id, more_info) VALUES (:combo_id, :more_info)");\n    $stmt->execute(['combo_id' => $index, 'more_info' => 'Item Name']);\n}