<?php\nfunction reduceArrayByAveraging($inputArray, $outputSize) {\n    $inputSize = count($inputArray);\n    if ($inputSize <= $outputSize) {\n        return $inputArray; // No reduction needed\n    }\n\n    $reducedArray = [];\n    $chunkSize = $inputSize / $outputSize;\n\n    for ($i = 0; $i < $outputSize; $i++) {\n        $start = floor($i * $chunkSize);\n        $end = floor(($i + 1) * $chunkSize);\n        \n        $sum = 0;\n        $count = 0;\n\n        for ($j = $start; $j < $end; $j++) {\n            if (isset($inputArray[$j])) {\n                $sum += $inputArray[$j];\n                $count++;\n            }\n        }\n\n        $average = $count > 0 ? $sum / $count : 0;\n        $reducedArray[] = $average;\n    }\n\n    return $reducedArray;\n}\n\n// Example usage:\n$randomList = array();\nfor ($i = 0; $i < 20; $i++) {\n    $randomList[] = mt_rand(1, 50);\n}\n\n$reducedList = reduceArrayByAveraging($randomList, 5);\nprint_r($reducedList);\n?>