<?php\nif ($_SERVER["REQUEST_METHOD"] == "POST") {\n    $monthlybill = floatval($_POST['monthlybill']);\n\n    // Include the file with the offer savings values\n    include 'values.php';\n\n    // Calculate savings for each offer\n    $savings = [];\n    for ($i = 1; $i <= 7; $i++) {\n        $offer_calcsavings = "offer" . $i . "calcsavings";\n        if (isset($$offer_calcsavings)) {\n            $savings[$i] = $monthlybill - $$offer_calcsavings;\n        } else {\n            $savings[$i] = 0;\n        }\n    }\n?>\n<!DOCTYPE html>\n<html lang="en">\n<head>\n    <meta charset="UTF-8">\n    <meta name="viewport" content="width=device-width, initial-scale=1.0">\n    <title>VoIP Savings Results</title>\n</head>\n<body>\n    <h1>VoIP Savings Results</h1>\n    <p>Your monthly bill was: $<?php echo number_format($monthlybill, 2); ?></p>\n    <table border="1">\n        <tr>\n            <th>Service</th>\n            <th>Annual Savings</th>\n        </tr>\n        <?php foreach ($savings as $offer_id => $annual_savings): ?>\n        <tr>\n            <td>Offer <?php echo $offer_id; ?></td>\n            <td>$<?php echo number_format($annual_savings * 12, 2); ?></td>\n        </tr>\n        <?php endforeach; ?>\n    </table>\n</body>\n</html>\n<?php\n} else {\n    echo "Invalid request.";\n}\n?>