// Using OAuth 2.0 for Yahoo API\n// You will need to use a library such as OAuth2-client or similar\n\n$client = new OAuth2\Client('your_client_id', 'your_client_secret');\n$accessToken = 'access_token_retrieved_via_oauth';\n\n$response = $client->fetch('https://social.yahooapis.com/v1/user/me/contacts', [], OAuth2\Client::HTTP_METHOD_GET, [\n    'Authorization' => 'Bearer ' . $accessToken,\n]);\n\nforeach ($response['contacts']['contact'] as $contact) {\n    $name = $contact['fields'][0]['value'];\n    echo "Name: $name\n";\n}