// Use mysqli for database connection\n$mysqli = new mysqli("localhost", "user", "password", "database");\n\n// Check connection\nif ($mysqli->connect_error) {\n    die("Connection failed: " . $mysqli->connect_error);\n}\n\n// Example of handling an integer input\n$integer = (int) $_POST['integer'];\n\n// Example of handling a string input\n$string = $_POST['string'];\n\n// Prepare an SQL statement with placeholders\n$stmt = $mysqli->prepare("INSERT INTO table_name (integer_column, string_column) VALUES (?, ?)");\n\n// Bind parameters (i for integer, s for string)\n$stmt->bind_param("is", $integer, $string);\n\n// Execute the statement\n$stmt->execute();\n\n// Close the statement and connection\n$stmt->close();\n$mysqli->close();