// Example of handling a string input\n$string = $_POST['string'];\n\n// Using mysqli_real_escape_string to escape special characters\n$escapedString = $mysqli->real_escape_string($string);\n\n// If you need to use the string in a LIKE query, escape wildcards\n$escapedString = str_replace(['%', '_'], ['\\%', '\\_'], $escapedString);\n\n// Now $escapedString is safe to use in your query