<?php\n\nfunction add_business_days($start_date, $days, $holidays = []) {\n    $date = new DateTime($start_date);\n    $holidays = array_map(function($holiday) {\n        return (new DateTime($holiday))->format('Y-m-d');\n    }, $holidays);\n    \n    while ($days > 0) {\n        $date->modify('+1 day');\n        $current_day = $date->format('N'); // 1 = Monday, ..., 7 = Sunday\n        $formatted_date = $date->format('Y-m-d');\n        \n        if ($current_day < 6 && !in_array($formatted_date, $holidays)) {\n            $days--;\n        }\n    }\n    \n    return $date->format('Y-m-d');\n}\n\n// Example usage:\n$start_date = '2023-12-05'; // Start date\n$business_days = 3; // Number of business days to add\n$us_holidays = [\n    '2023-01-01', // New Year's Day\n    '2023-07-04', // Independence Day\n    '2023-12-25', // Christmas Day\n    // Add other US federal holidays as needed\n];\n\n$result_date = add_business_days($start_date, $business_days, $us_holidays);\necho $result_date; // Output: 2023-12-10\n\n?>