<?php\n// Establish a secure database connection using PDO\ntry {\n    $pdo = new PDO('mysql:host=your_host;dbname=your_dbname', 'your_username', 'your_password');\n    $pdo->setAttribute(PDO::ATTR_ERRMODE, PDO::ERRMODE_EXCEPTION);\n} catch (PDOException $e) {\n    echo 'Connection failed: ' . $e->getMessage();\n}\n\n// Query to fetch roles from the users table\n$sql = "SELECT role FROM users";\n$stmt = $pdo->query($sql);\n\nif ($stmt->rowCount() > 0) {\n    while ($row = $stmt->fetch(PDO::FETCH_ASSOC)) {\n        $role = $row['role'];\n        echo "<select name='role'>";\n        echo "\r\n<option value='Resident Assistant'" . ($role == 'Resident Assistant' ? " selected" : "") . ">Resident Assistant</option>";\n        echo "\r\n<option value='Community Assistant'" . ($role == 'Community Assistant' ? " selected" : "") . ">Community Assistant</option>";\n        echo "\r\n<option value='Head RA'" . ($role == 'Head RA' ? " selected" : "") . ">Head RA</option>";\n        echo "\r\n</select>";\n        echo "<br>"; // Add a line break for clarity\n    }\n}\n?>