<?php\n\n// URL for login\n$loginUrl = 'http://highlearn.website/login_page.asp?userID=foo&password=bar';\n\n// Initialize cURL session\n$ch = curl_init();\n\n// Setting options for cURL session\ncurl_setopt($ch, CURLOPT_URL, $loginUrl);\ncurl_setopt($ch, CURLOPT_RETURNTRANSFER, true);\ncurl_setopt($ch, CURLOPT_HEADER, true);\ncurl_setopt($ch, CURLOPT_COOKIEJAR, 'cookie.txt'); // Save cookies to a file\n\n// Execute login request\n$response = curl_exec($ch);\n\n// Check for errors\nif (curl_errno($ch)) {\n    echo 'Error:' . curl_error($ch);\n}\n\n// Close the cURL session\ncurl_close($ch);\n\n// URL for the page that requires login\n$protectedUrl = 'http://highlearn.website/protected_page.asp';\n\n// Initialize another cURL session\n$ch = curl_init();\n\n// Setting options to use saved cookies\ncurl_setopt($ch, CURLOPT_URL, $protectedUrl);\ncurl_setopt($ch, CURLOPT_RETURNTRANSFER, true);\ncurl_setopt($ch, CURLOPT_COOKIEFILE, 'cookie.txt'); // Use cookies from the file\n\n// Execute request to protected page\n$response = curl_exec($ch);\n\n// Check for errors\nif (curl_errno($ch)) {\n    echo 'Error:' . curl_error($ch);\n} else {\n    // Output the response from the protected page\n    echo $response;\n}\n\n// Close the cURL session\ncurl_close($ch);\n\n?>