// Connect to Redis\n    $redis = new Redis();\n    $redis->connect('127.0.0.1', 6379);\n\n    // Store data in Redis (example data)\n    $data = [\n        'field1' => 'value1',\n        'field2' => 'value2',\n        // ... up to 200 fields\n    ];\n\n    // Store data in a cache key\n    $cacheKey = 'cache_key_for_data';\n    $redis->hmset($cacheKey, $data);\n\n    // Optionally, you can set an expiry time for the cache\n    $redis->expire($cacheKey, 3600); // 1 hour expiry