// Example using RabbitMQ to push a job to a queue\n    $connection = new AMQPStreamConnection('localhost', 5672, 'guest', 'guest');\n    $channel = $connection->channel();\n\n    $channel->queue_declare('write_behind_queue', false, true, false, false);\n\n    $jobData = json_encode(['cacheKey' => $cacheKey]);\n    $msg = new AMQPMessage($jobData, ['delivery_mode' => 2]);\n\n    $channel->basic_publish($msg, '', 'write_behind_queue');\n\n    $channel->close();\n    $connection->close();