public function query($queryString)\n{\n    if (!$this->_connected) {\n        $this->_connectToDb(); // connect to database\n    }\n\n    $results = mysqli_query($this->_dbLink, $queryString) or trigger_error(mysqli_error($this->_dbLink));\n\n    $resultArray = [];\n    if (mysqli_num_rows($results) > 0) {\n        while ($row = mysqli_fetch_assoc($results)) {\n            $resultArray[] = $row;\n        }\n    }\n\n    return !empty($resultArray) ? $resultArray : false;\n}