<?php\nrequire_once 'vendor/autoload.php';\n\n// Configure HTML Purifier\n$config = HTMLPurifier_Config::createDefault();\n$config->set('HTML.Allowed', 'b,strong'); // Allow only <b> and <strong> tags\n\n$purifier = new HTMLPurifier($config);\n\n// Sample user input\n$user_input = '<b> 5 > 3 = true</b>';\n\n// Purify the user input\n$clean_html = $purifier->purify($user_input);\n\n// Display the result\necho $clean_html;