<?php\nfunction timeAgo($datetime) {\n    $now = new DateTime();\n    $posted = new DateTime($datetime);\n    $diff = $now->diff($posted);\n\n    if ($diff->y > 0) {\n        return $diff->y . ' year(s) ago';\n    } elseif ($diff->m > 0) {\n        return $diff->m . ' month(s) ago';\n    } elseif ($diff->d > 0) {\n        return $diff->d . ' day(s) ago';\n    } elseif ($diff->h > 0) {\n        return $diff->h . ' hour(s) ago';\n    } elseif ($diff->i > 0) {\n        return $diff->i . ' minute(s) ago';\n    } else {\n        return $diff->s . ' second(s) ago';\n    }\n}\n\n// Example usage\n$published_date = '2008-11-03T21:30:06Z';\necho timeAgo($published_date);\n?>