<?php\n$source_file_path = 'http://cache2.mycrib.net/images/image_group92/0/43/653807d5727a46498180e8ef57fdf7819b2b0c.jpg';\n$watermark_image = 'fpwatermark.gif'; // the watermark image\n$destination_image = 'coooolgif.gif'; // where to save new file\n\n// Load the source image\n$image = imagecreatefromjpeg($source_file_path);\nif (!$image) {\n    die("Failed to load the source image.");\n}\n\n// Load the watermark image\n$watermark = imagecreatefromgif($watermark_image);\nif (!$watermark) {\n    die("Failed to load the watermark image.");\n}\n\n// Get the size of the source image and watermark image\n$image_width = imagesx($image);\n$image_height = imagesy($image);\n$watermark_width = imagesx($watermark);\n$watermark_height = imagesy($watermark);\n\n// Calculate the position of the watermark on the source image\n$watermark_x = $image_width - $watermark_width - 10; // 10px from the right\n$watermark_y = $image_height - $watermark_height - 10; // 10px from the bottom\n\n// Merge the watermark with the source image\nimagecopy($image, $watermark, $watermark_x, $watermark_y, 0, 0, $watermark_width, $watermark_height);\n\n// Save the new image\nif (!imagejpeg($image, $destination_image)) {\n    die("Failed to save the watermarked image.");\n}\n\n// Free up memory\nimagedestroy($image);\nimagedestroy($watermark);\n\necho "Watermark applied successfully.";\n?>