<?php\nfunction getTopGoogleResult($query, $apiKey, $cx) {\n    $url = 'https://www.googleapis.com/customsearch/v1?q=' . urlencode($query) . '&key=' . $apiKey . '&cx=' . $cx;\n    $response = file_get_contents($url);\n    $data = json_decode($response, true);\n    \n    if (isset($data['items'][0]['link'])) {\n        return $data['items'][0]['link'];\n    } else {\n        return null;\n    }\n}\n\n$apiKey = 'YOUR_API_KEY';\n$cx = 'YOUR_SEARCH_ENGINE_ID';\n$query = 'YOUR_SEARCH_QUERY';\n\n$topResultUrl = getTopGoogleResult($query, $apiKey, $cx);\n\nif ($topResultUrl) {\n    header("Location: $topResultUrl");\n    exit();\n} else {\n    echo "No results found.";\n}\n?>