// Example of conditional includes\n$page = $_GET['page'] ?? 'default';\n\nswitch ($page) {\n    case 'home':\n        require_once 'functions/home_functions.php';\n        break;\n    case 'about':\n        require_once 'functions/about_functions.php';\n        break;\n    // Add more cases as needed\n    default:\n        require_once 'functions/default_functions.php';\n        break;\n}