require_once 'vendor/autoload.php';\n\n$client = new Google_Client();\n$client->setApplicationName('Your Application Name');\n$client->setScopes(Google_Service_PeopleService::CONTACTS_READONLY);\n$client->setAuthConfig('path/to/credentials.json');\n$client->setAccessType('offline');\n\n// Handle OAuth 2.0 authorization\n$service = new Google_Service_PeopleService($client);\n$results = $service->people_connections->listPeopleConnections('people/me', array('pageSize' => 10));\n\nforeach ($results->getConnections() as $person) {\n    if (count($person->getNames()) == 0) {\n        continue;\n    }\n    $name = $person->getNames()[0]->getDisplayName();\n    echo "Name: $name\n";\n}