function replaceTags($template, $newtext) {\n    // Escape $ signs in the newtext\n    $escaped_newtext = str_replace('$', '\\$', $newtext);\n    \n    // Perform the replacement\n    $template = preg_replace('/%MYTAG%/', $escaped_newtext, $template);\n    \n    return $template;\n}\n\n$template = "Replace this: %MYTAG%";\n$newtext = "I earn $1,000,000 a year";\n\necho replaceTags($template, $newtext);\n\n// RESULT\n// Replace this: I earn $1,000,000 a year