<?php\n// Create a Memcache object\n$memcache = new Memcache;\n$memcache->connect('localhost', 11211) or die('Could not connect');\n\n// Create an array\n$user_data = array(\n    'user_id' => 123,\n    'photo_url' => 'http://example.com/photo.jpg',\n    'name' => 'John Doe'\n);\n\n// Store the array in Memcache\n$memcache->set('user_data', $user_data);\n\n// Retrieve the array from Memcache\n$retrieved_user_data = $memcache->get('user_data');\n\n// Display the retrieved array\nprint_r($retrieved_user_data);\n?>