<?php\nrequire 'vendor/autoload.php';\n\nuse PhpOffice\PhpSpreadsheet\Spreadsheet;\nuse PhpOffice\PhpSpreadsheet\Writer\Xlsx;\nuse PhpOffice\PhpSpreadsheet\Style\NumberFormat;\n\n// Create a new spreadsheet\n$spreadsheet = new Spreadsheet();\n$sheet = $spreadsheet->getActiveSheet();\n\n// Set the value of a cell to a date\n$dateValue = \PhpOffice\PhpSpreadsheet\Shared\Date::PHPToExcel(time());\n$sheet->setCellValue('A1', $dateValue);\n\n// Apply a date format to the cell\n$sheet->getStyle('A1')->getNumberFormat()->setFormatCode(NumberFormat::FORMAT_DATE_YYYYMMDD2);\n\n// Save the spreadsheet\n$writer = new Xlsx($spreadsheet);\n$writer->save('example.xlsx');\n?>