<?php\nsession_start();\n\nif (!isset($_GET['cmd']) || !isset($_GET['pk'])) {\n    die("You should have both 'cmd' and 'pk' parameters in your URL");\n}\n\n$cmd = $_GET['cmd'];\n$pk = (int)$_GET['pk'];  // Cast to int to avoid SQL injection\n\n// Use MySQLi or PDO instead of deprecated mysql_* functions\n$con = new mysqli("localhost", "root", "geheim", "ebay");\n\nif ($con->connect_error) {\n    die('Connection failed: ' . $con->connect_error);\n}\n\nif ($cmd == "GetAuctionData") {\n    echo "<h2>Username</h2><h2>Start Date</h2><h2>Description</h2>";\n    \n    $stmt = $con->prepare("SELECT * FROM Auctions WHERE ARTICLE_NO = ?");\n    $stmt->bind_param("i", $pk);\n    $stmt->execute();\n    $result = $stmt->get_result();\n\n    while ($row = $result->fetch_assoc()) {\n        echo "<div>";\n        echo "<p>Username: " . htmlspecialchars($row['USERNAME']) . "</p>";\n        echo "<p>Article Number: " . htmlspecialchars($row['ARTICLE_NO']) . "</p>";\n        echo "<p>Article Name: " . htmlspecialchars($row['ARTICLE_NAME']) . "</p>";\n        echo "<p>Subtitle: " . htmlspecialchars($row['SUBTITLE']) . "</p>";\n        echo "<p>Current Bid: " . htmlspecialchars($row['CURRENT_BID']) . "</p>";\n        echo "<p>Start Price: " . htmlspecialchars($row['START_PRICE']) . "</p>";\n        echo "<p>Bid Count: " . htmlspecialchars($row['BID_COUNT']) . "</p>";\n        echo "<p>Total Quantity: " . htmlspecialchars($row['QUANT_TOTAL']) . "</p>";\n        echo "<p>Sold Quantity: " . htmlspecialchars($row['QUANT_SOLD']) . "</p>";\n        echo "<p>Access Starts: " . htmlspecialchars($row['ACCESSSTARTS']) . "</p>";\n        echo "<p>Access Ends: " . htmlspecialchars($row['ACCESSENDS']) . "</p>";\n        echo "<p>Description: " . htmlspecialchars($row['ARTICLE_DESC']) . "</p>";\n        \n        // Display the image\n        $pic_url = htmlspecialchars($row['PIC_URL']);\n        if (!empty($pic_url)) {\n            echo "<img src='$pic_url' alt='Auction Image' />";\n        } else {\n            echo "<img src='placeholder.jpg' alt='No Image Available' />";\n        }\n\n        echo "</div>";\n    }\n\n    $stmt->close();\n}\n\n$con->close();\n?>