<?php\nfunction sanitize_comment_meta($commentdata) {\n    if (isset($_POST['friend_name'])) {\n        $commentdata['friend_name'] = sanitize_text_field($_POST['friend_name']);\n    }\n    if (isset($_POST['friend_email'])) {\n        $commentdata['friend_email'] = sanitize_email($_POST['friend_email']);\n    }\n    return $commentdata;\n}\nadd_filter('preprocess_comment', 'sanitize_comment_meta');\n?>