<?php\nfunction send_comment_email($comment_id) {\n    $comment = get_comment($comment_id);\n    $friend_name = isset($_POST['friend_name']) ? sanitize_text_field($_POST['friend_name']) : '';\n    $friend_email = isset($_POST['friend_email']) ? sanitize_email($_POST['friend_email']) : '';\n    \n    if (!empty($friend_email)) {\n        $subject = "Hey $friend_name, your friend mentioned you!";\n        $message = "Hey $friend_name,\n\nYour friend mentioned you in a comment:\n\n" . $comment->comment_content . "\n\nIsn't that cool?";\n        $headers = 'From: ' . get_option('admin_email') . "\r\n";\n        \n        wp_mail($friend_email, $subject, $message, $headers);\n    }\n}\nadd_action('comment_post', 'send_comment_email', 10, 1);\n?>