<?php\nfunction custom_comment_form_fields($fields) {\n    $fields['friend_name'] = '<p class="comment-form-friend-name"><label for="friend_name">' . __( 'Friend\'s Name' ) . '</label> ' .\n        '<input id="friend_name" name="friend_name" type="text" value="" size="30" /></p>';\n    $fields['friend_email'] = '<p class="comment-form-friend-email"><label for="friend_email">' . __( 'Friend\'s Email' ) . '</label> ' .\n        '<input id="friend_email" name="friend_email" type="email" value="" size="30" /></p>';\n    return $fields;\n}\nadd_filter('comment_form_default_fields', 'custom_comment_form_fields');\n?>