function testParam($fruit, $veg = null, $test = 'default test') {\n    if (is_null($veg)) {\n        $veg = 'pota';\n    }\n    echo '<br>$fruit = '.$fruit;\n    echo '<br>$veg = '.$veg;\n    echo '<br>Test = '.$test;\n}\n\n// Call with all parameters\necho 'with all parameters';\ntestParam('apple', 'carrot', 'some string');\n// Output:\n// $fruit = apple\n// $veg = carrot\n// Test = some string\n\n// Call with missing veg\necho '<hr> missing veg';\ntestParam('apple', null, 'something');\n// Output:\n// $fruit = apple\n// $veg = pota\n// Test = something\n\n// Desired call with skipped $veg\necho '<hr> This works';\ntestParam('apple', null, 'i am set');\n// Output:\n// $fruit = apple\n// $veg = pota\n// Test = i am set