<?php\n\nfunction getThumbnailPath($filePath) {\n    $fileInfo = pathinfo($filePath);\n    $thumbnailPath = $fileInfo['dirname'] . '/' . $fileInfo['filename'] . '_thumb.' . $fileInfo['extension'];\n    return $thumbnailPath;\n}\n\n// Example usage\n$file = 'http://file-managers.net/skins/blue/images/actions/view_tree.png';\n$thumbnail = getThumbnailPath($file);\n\n// will show;\n// http://file-managers.net/skins/blue/images/actions/view_tree_thumb.png\necho $thumbnail;\n\n?>