<?php\nfunction remove_excess_whitespace($content) {\n    // Remove multiple blank lines\n    $content = preg_replace('/\n\s*\n/', "\n\n", $content);\n    return $content;\n}\n\n$input_file = 'path/to/your/code.php';\n$output_file = 'path/to/your/clean_code.php';\n\n$code = file_get_contents($input_file);\n$clean_code = remove_excess_whitespace($code);\nfile_put_contents($output_file, $clean_code);\n\necho "Excess whitespace removed successfully!";\n?>