function getArrayDepth(array $array): int {\n    $maxDepth = 1; // Initial depth\n\n    foreach ($array as $element) {\n        if (is_array($element)) {\n            $depth = getArrayDepth($element) + 1;\n            if ($depth > $maxDepth) {\n                $maxDepth = $depth;\n            }\n        }\n    }\n\n    return $maxDepth;\n}\n\n// Example usage\n$array = [1, [2, [3, [4]]], [5]];\necho getArrayDepth($array); // Output: 4