<?php\n\nfunction getUlContent($url) {\n    $content = file_get_contents($url);\n    if ($content === FALSE) {\n        return "Failed to retrieve content from $url";\n    }\n\n    $dom = new DOMDocument();\n    libxml_use_internal_errors(true); // Suppress warnings from invalid HTML\n    $dom->loadHTML($content);\n    libxml_clear_errors();\n\n    $ulElements = $dom->getElementsByTagName('ul');\n    $ulContent = '';\n\n    foreach ($ulElements as $ul) {\n        $ulContent .= $dom->saveHTML($ul);\n    }\n\n    return $ulContent;\n}\n\n$tracContent = getUlContent('http://myserver/trac');\n$svnContent = getUlContent('http://myserver/svn');\n?>\n\n<!DOCTYPE html>\n<html>\n<head>\n    <title>Index Page</title>\n</head>\n<body>\n    <h1>Trac Content</h1>\n    <?php echo $tracContent; ?>\n\n    <h1>SVN Content</h1>\n    <?php echo $svnContent; ?>\n</body>\n</html>