<?php\n// Example of $row2["ARTICLE_DESC"]\n$row2["ARTICLE_DESC"] = "Your HTML content here with properly escaped quotes and special characters";\n\n// PHP code to generate the JavaScript function\necho '\n<script type="text/javascript">\nfunction makewindows() {\n    var child1 = window.open("about:blank");\n    var content = ' . json_encode($row2["ARTICLE_DESC"]) . ';\n    child1.document.write(content);\n    child1.document.close();\n}\n</script>\n';\n?>\n\n<!-- HTML to call the function -->\n<button onclick="makewindows()">Open Popup</button>