<?php\n$encryption_key = getenv('ENCRYPTION_KEY');\n\nif ($encryption_key === false) {\n    die('Encryption key not found in environment variables.');\n}\n\n// Function to encrypt data\nfunction encryptData($data, $key) {\n    $cipher = "aes-256-cbc";\n    $iv = openssl_random_pseudo_bytes(openssl_cipher_iv_length($cipher));\n    $encrypted = openssl_encrypt($data, $cipher, $key, 0, $iv);\n    return base64_encode($iv . $encrypted);\n}\n\n// Function to decrypt data\nfunction decryptData($data, $key) {\n    $cipher = "aes-256-cbc";\n    $data = base64_decode($data);\n    $iv_length = openssl_cipher_iv_length($cipher);\n    $iv = substr($data, 0, $iv_length);\n    $encrypted = substr($data, $iv_length);\n    return openssl_decrypt($encrypted, $cipher, $key, 0, $iv);\n}\n\n// Example usage\n$plain_text = "Sensitive data";\n$encrypted_text = encryptData($plain_text, $encryption_key);\n$decrypted_text = decryptData($encrypted_text, $encryption_key);\n\necho "Original: $plain_text\n";\necho "Encrypted: $encrypted_text\n";\necho "Decrypted: $decrypted_text\n";\n?>