<?php\n\n// List of profane words\n$badWords = ["badword1", "badword2", "a$$", "azz", "a55"];\n\n// Function to check for profanity\nfunction containsProfanity($text, $badWords) {\n    foreach ($badWords as $word) {\n        // Use regular expressions to catch variations\n        $pattern = "/\b" . preg_quote($word, '/') . "\b/i";\n        if (preg_match($pattern, $text)) {\n            return true;\n        }\n    }\n    return false;\n}\n\n// Example usage\n$inputText = "This is an example input containing badword1.";\nif (containsProfanity($inputText, $badWords)) {\n    echo "Profanity detected!";\n} else {\n    echo "No profanity detected.";\n}\n?>