<?php\ninclude("pChart2.1.4/class/pData.class.php");\ninclude("pChart2.1.4/class/pDraw.class.php");\ninclude("pChart2.1.4/class/pImage.class.php");\n\n$data = new pData();\n$data->addPoints(array(12, 19, 3, 5, 2, 3), "Values");\n$data->setAxisName(0, "Value");\n$data->addPoints(array("Red", "Blue", "Yellow", "Green", "Purple", "Orange"), "Labels");\n$data->setSerieDescription("Labels", "Category");\n$data->setAbscissa("Labels");\n\n$image = new pImage(400, 200, $data);\n$image->setFontProperties(array("FontName"=>"pChart2.1.4/fonts/verdana.ttf","FontSize"=>10));\n$image->drawText(150, 35, "Bar Chart Example", array("FontSize"=>20,"Align"=>TEXT_ALIGN_BOTTOMMIDDLE));\n\n$settings = array("Gradient"=>TRUE, "DisplayPos"=>LABEL_POS_INSIDE, "DisplayValues"=>TRUE, "DisplayR"=>0, "DisplayG"=>0, "DisplayB"=>0, "DisplayShadow"=>TRUE,"Surrounding"=>20);\n$image->drawBarChart($settings);\n\n$image->Render("example.png");\n?>