require_once('lib/nusoap.php');\n\n// Create a new client instance\n$soap = new nusoap_client('http://localhost/test/webservice_user.php?wsdl', true);\n\n// Check for errors\n$err = $soap->getError();\nif ($err) {\n    echo "SOAP Constructor Error: $err";\n    exit();\n}\n\n// Call a SOAP method\n$user = $soap->call('getUser', array('userId' => 1));\n\n// Check for faults\nif ($soap->fault) {\n    echo "SOAP Fault: ";\n    print_r($user);\n} else {\n    // Check for errors\n    $err = $soap->getError();\n    if ($err) {\n        echo "SOAP Error: $err";\n    } else {\n        // Use the result\n        print_r($user);\n    }\n}