$mysqli = new mysqli("localhost", "username", "password", "database");\n\nif ($mysqli->connect_error) {\n    die("Connection failed: " . $mysqli->connect_error);\n}\n\n$userID = intval($userID); // Ensure $userID is an integer to prevent SQL injection\n$query = $mysqli->prepare("SELECT * FROM messages WHERE recipient = ? AND `read` = 0");\n$query->bind_param("i", $userID);\n$query->execute();\n$result = $query->get_result();\n$msgs = $result->num_rows;\n\necho $msgs;\n\n$query->close();\n$mysqli->close();