<?php\nfunction countUppercaseWords($string) {\n    // Use preg_match_all with a regular expression to find all uppercase words\n    preg_match_all('/\b[A-Z]+\b/', $string, $matches);\n    // Return the count of uppercase words found\n    return count($matches[0]);\n}\n\n// Example usage\n$string = "THIS is A Test STRING with SOME UPPERCASE words.";\necho countUppercaseWords($string); // Output: 6\n?>