<?php\n\nfunction imperialToMetric($length) {\n    // Convert the string to lowercase to handle cases consistently\n    $length = strtolower($length);\n\n    // Initialize variables\n    $feet = 0;\n    $inches = 0;\n    $fraction = 0;\n\n    // Regular expression to match the length format\n    $pattern = '/((?<feet>\d+)\')?\s*(?<inches>\d+)?\s*(?<fraction>\d+\/\d+)?\s*\"?/';\n\n    if (preg_match($pattern, $length, $matches)) {\n        // Extract feet, inches, and fraction from matches\n        if (!empty($matches['feet'])) {\n            $feet = (int)$matches['feet'];\n        }\n        if (!empty($matches['inches'])) {\n            $inches = (int)$matches['inches'];\n        }\n        if (!empty($matches['fraction'])) {\n            list($numerator, $denominator) = explode('/', $matches['fraction']);\n            $fraction = $numerator / $denominator;\n        }\n    }\n\n    // Convert the total length to inches\n    $totalInches = ($feet * 12) + $inches + $fraction;\n\n    // Convert inches to centimeters (1 inch = 2.54 cm)\n    $totalCm = $totalInches * 2.54;\n\n    return $totalCm;\n}\n\n// Example usage\n$length1 = "3' 2 1/2\"";\n$length2 = "1/2\"";\n\necho imperialToMetric($length1) . " cm\n"; // Output: 97.79 cm\necho imperialToMetric($length2) . " cm\n"; // Output: 1.27 cm\n\n?>