require_once __DIR__ . '/vendor/autoload.php'; // Ensure you have installed mpdf via Composer\n\nuse Mpdf\Mpdf;\n\n$html = '<h1>My PDF</h1><p>This is a sample PDF generated using mpdf.</p>';\n\ntry {\n    $mpdf = new Mpdf();\n    $mpdf->WriteHTML($html);\n    $mpdf->Output('document.pdf', 'D'); // 'D' to download the file directly\n} catch (\Mpdf\MpdfException $e) {\n    echo $e->getMessage();\n}