// Domain/PersonFactory.php\n   class PersonFactory {\n       public static function create($id, $userType) {\n           switch ($userType) {\n               case 'admin':\n                   return new Admin($id, $userType);\n               case 'buyer':\n                   return new Buyer($id, $userType);\n               case 'associate':\n                   return new Associate($id, $userType);\n               case 'supervisor':\n                   return new Supervisor($id, $userType);\n               default:\n                   return new Person($id, $userType);\n           }\n       }\n   }