<?php\n// Create (if not exists) and open SQLite database connection\n$db = new SQLite3('counter.db');\n\n// Create table if it doesn't exist\n$db->exec("CREATE TABLE IF NOT EXISTS counter (id INTEGER PRIMARY KEY, count INTEGER)");\n\n// Initialize counter if it doesn't exist\n$result = $db->query("SELECT count FROM counter WHERE id = 1");\nif ($result->fetchArray(SQLITE3_ASSOC) === false) {\n    $db->exec("INSERT INTO counter (id, count) VALUES (1, 0)");\n}\n\n// Increment counter\n$db->exec("UPDATE counter SET count = count + 1 WHERE id = 1");\n\n// Get current count\n$result = $db->query("SELECT count FROM counter WHERE id = 1");\n$row = $result->fetchArray(SQLITE3_ASSOC);\n$currentCount = $row['count'];\n\n// Output the current count\necho "Current count: " . $currentCount;\n\n// Close the database connection\n$db->close();\n?>