<?php\n// URL of the web page to retrieve\n$url = "http://example.com";\n\n// Initialize cURL session\n$ch = curl_init($url);\n\n// Set cURL options\ncurl_setopt($ch, CURLOPT_RETURNTRANSFER, true);\n\n// Execute cURL session\n$content = curl_exec($ch);\n\n// Check for errors\nif (curl_errno($ch)) {\n    die("Error: " . curl_error($ch));\n}\n\n// Close cURL session\ncurl_close($ch);\n\n// The text to search for\n$searchText = "certain text";\n\n// Search for the text in the content\nif (strpos($content, $searchText) !== FALSE) {\n    echo "Text found!";\n} else {\n    echo "Text not found.";\n}\n?>