<?php\n// Database connection details\n$host = 'localhost';\n$db = 'shared_database';\n$user = 'db_user';\n$pass = 'db_password';\n\n// Connect to the database\n$mysqli = new mysqli($host, $user, $pass, $db);\n\n// Check connection\nif ($mysqli->connect_error) {\n    die("Connection failed: " . $mysqli->connect_error);\n}\n\n// Fetch articles from Wikia\n$wiki_query = "SELECT * FROM wikia_articles";\n$wiki_result = $mysqli->query($wiki_query);\n\nif ($wiki_result->num_rows > 0) {\n    while($row = $wiki_result->fetch_assoc()) {\n        // Prepare WordPress insert query\n        $wp_query = "INSERT INTO wp_posts (post_title, post_content, post_category, post_status)\n                     VALUES (?, ?, ?, 'publish')";\n        \n        // Prepare statement\n        $stmt = $mysqli->prepare($wp_query);\n        $category_id = 3; // example category ID for imported articles\n        $stmt->bind_param('ssi', $row['title'], $row['content'], $category_id);\n        \n        // Execute statement\n        if ($stmt->execute()) {\n            echo "Article imported successfully: " . $row['title'] . "\n";\n        } else {\n            echo "Error importing article: " . $stmt->error . "\n";\n        }\n    }\n} else {\n    echo "No articles found in Wikia database.\n";\n}\n\n// Close connection\n$mysqli->close();\n?>