// Example in a CakePHP controller\npublic function upload() {\n    if ($this->request->is('post')) {\n        // Assuming the image file input name is 'image'\n        $image = $this->request->data['ModelName']['image'];\n\n        // Get image dimensions\n        list($width, $height) = getimagesize($image['tmp_name']);\n\n        // Define your desired dimensions\n        $maxWidth = 800;\n        $maxHeight = 600;\n\n        if ($width > $maxWidth || $height > $maxHeight) {\n            $this->Flash->error(__('The uploaded image exceeds the maximum allowed dimensions.'));\n        } else {\n            // Proceed with your file upload logic\n            // e.g., move_uploaded_file($image['tmp_name'], $destination);\n            $this->Flash->success(__('The image has been uploaded successfully.'));\n        }\n    }\n}