<?php\n$environment = getenv('APPLICATION_ENV');\n\nswitch ($environment) {\n    case 'development':\n        require 'config/config.development.php';\n        break;\n    case 'production':\n        require 'config/config.production.php';\n        break;\n    // Add other environments as needed\n    default:\n        throw new Exception('Unknown environment: ' . $environment);\n}\n\n// Now you can use variables defined in the included config file