<?php\n$environment = getenv('APPLICATION_ENV');\n\nswitch ($environment) {\n    case 'development':\n        ini_set('display_errors', 1);\n        error_reporting(E_ALL);\n        $dbConfig = [\n            'host' => 'localhost',\n            'username' => 'dev_user',\n            'password' => 'dev_pass',\n            'dbname' => 'dev_db'\n        ];\n        break;\n    case 'production':\n        ini_set('display_errors', 0);\n        error_reporting(0);\n        $dbConfig = [\n            'host' => 'prod_db_host',\n            'username' => 'prod_user',\n            'password' => 'prod_pass',\n            'dbname' => 'prod_db'\n        ];\n        break;\n    // Add other environments as needed\n    default:\n        throw new Exception('Unknown environment: ' . $environment);\n}\n\n// Now you can use $dbConfig for your database connectio